
$(function(){$.fn.carousel=function(options){var defaults={fontSize:'16px',color:'white',backgroundColor:'rgb(0, 155, 255)',animateLeft:500,animateRight:500};var settings=$.extend(defaults,options);var $buttonLeft=$('.carousel-arrow-left');var $buttonRight=$('.carousel-arrow-right');var $elementsList=$('.carousel-list');var $pixelsOffset=1024;var $currentLeftValue=0;var $elementsCount=$elementsList.find('li').length;var $minimumOffset=-(($elementsCount-1)*$pixelsOffset);var $maximumOffset=0;$buttonLeft.css({'font-size':settings.fontSize,'background-color':settings.backgroundColor,'color':settings.color});$buttonRight.css({'font-size':settings.fontSize,'background-color':settings.backgroundColor,'color':settings.color});$buttonLeft.click(function(){if($currentLeftValue!=$maximumOffset){$currentLeftValue+=1024;$elementsList.animate({left:$currentLeftValue+"px"},settings.animateLeft);}});$buttonRight.click(function(){if($currentLeftValue!=$minimumOffset){$currentLeftValue-=1024;$elementsList.animate({left:$currentLeftValue+"px"},settings.animateRight);}
return this;});};})(jQuery);$(function(){$('.carousel-list').carousel({fontSize:'25px',animateLeft:1500});});